mapper.select:

SELECT  n.notice_id,n.notice_name,n.zipfile_name,p.app_no,p.patent_name

FROM notices n join patent p on n.patent= p.patent_id

 WHERE notice_id=#{noticeId}

select patent_first_column_id
	from sell_second_column 
		where patent_second_column_id = #{patent_second_column_id};

select s.price ,p.patent_name,s.patent_id
	from sell_patent_goods s join patents p on s.patent_id=p.patent_id
		where s.patent_second_column = #{id};

select s.price ,p.patent_name,s.patent_id
	from sell_patent_goods s join patents p on s.patent_id=p.patent_id
		where s.patent_second_column = #{id};

SELECT  sg.patent_id,sg.price,sg.status,sg.transaction_type,sg.add_date,sg.transaction_date,sg.transferor,p.patent_name,p.app_no,sg.recommend_status,ss.patent_first_column_id,sg.patent_second_column,sg.description
FROM  sell_patent_goods sg LEFT JOIN patents p ON sg.patent_id = p.patent_id left JOIN sell_second_column  ss ON ss.patent_second_column_id=sg.patent_second_column
WHERE sg.recommend_status=1 and sg.patent_second_column=#{id} limit 0,7;

select spg.patent_id,p.patent_name,spg.price,spg.patent_second_column,ssc.patent_second_column_name,ssc.patent_first_column_id,sfc.patent_first_column_name,spg.status,spg.patent_image_url,,spg.description from sell_patent_goods spg left join patents p on spg.patent_id=p.patent_id left join sell_second_column ssc on ssc.patent_second_column_id=spg.patent_second_column left join sell_first_column sfc on ssc.patent_first_column_id=sfc.patent_first_column_id where spg.status=3
and (p.patent_name like #{keyword_pattern} or p.app_no like #{keyword_pattern})
limit #{page.startIndex},#{page.pageSize}

select spg.patent_id,p.patent_name,p.app_no,p.app_date,p.app_person,p.publish_date,pt.patent_type_id,pt.patent_type_desc,spg.price,spg.patent_second_column,ssc.patent_second_column_name,ssc.patent_first_column_id,sfc.patent_first_column_name,spg.transferor,spg.status,spg.patent_image_url,spg.description from sell_patent_goods spg left join patents p on spg.patent_id=p.patent_id left join patent_types pt on p.patent_type=pt.patent_type_id left join sell_second_column ssc on ssc.patent_second_column_id=spg.patent_second_column left join sell_first_column sfc on ssc.patent_first_column_id=sfc.patent_first_column_id where spg.patent_second_column=#{secondColumnId} and spg.status=1
and (p.patent_name like #{keyword_pattern} or p.app_no like #{keyword_pattern})
limit #{page.startIndex},#{page.pageSize}
